function reconcileChildren(wipFiber: Fiber, elements: Element[]) {
  let index = 0

  let oldFiber = wipFiber.alternate && wipFiber.alternate.child

  let prevSibling: Fiber | null = null

  while (index < elements.length || !!oldFiber) {
    const element = elements[index]

    let newFiber: Fiber | null = null

    // compare old fiber to element
    const sameType = oldFiber && element && oldFiber.type === element.type
    if (oldFiber && element && oldFiber.type === element.type) {
      // update the node
      newFiber = {
        type: oldFiber.type,
        props: element.props,
        dom: oldFiber.dom,
        parent: wipFiber,
        alternate: oldFiber,
        effectTag: "UPDATE",
        child: null,
        sibling: null
      }
    } else {
      if (element && !sameType) {
        // todo add this node
      }

      if (oldFiber && !sameType) {
        // delete the old fiber node
        oldFiber.effectTag = "DELETION"
        setDeletions([...getDeletions(), oldFiber])
      }
    }


    if (index === 0) {
      wipFiber.child = newFiber
    } else {
      prevSibling && (prevSibling.sibling = newFiber)
    }

    prevSibling = newFiber
    // set element to next
    index++
    // set fiber to next
    if (oldFiber) {
      oldFiber = oldFiber.sibling
    }
  }
}
